import React, { Component } from 'react'
import { Form } from 'react-bootstrap';

class OurInput extends Component {
    constructor(){
        super();
        this.type = '';
    }

    render() {
        const{ type, name, labelTrue, labelFalse, title, choices } = this.props;


        return (
            <Form.Group>
            <Form.Label>{ name }</Form.Label>
            {
            (type === "text" && 
            <Form.Control as="textarea" rows="3"></Form.Control>
            )
            ||
            (type === "dropdown" && <Form.Control as="select"/>)
            ||
            (type === "boolean" && <Form.Check inline label={labelTrue} type="radio"/>
            )
            }
            </Form.Group>
        )
    }
}

export default OurInput;
