    }

    render() {
        const{ type, name, labelTrue, labelFalse, title, choices, idInput } = this.props;

        console.log(idInput);
        return (
            <Form.Group>
            {
                (name.includes("question") && <Form.Label key={idInput}>{ title }</Form.Label>)
                || <Form.Label key={idInput}>{ name }</Form.Label>

            }
            {
                (type === "text" && 
                <Form.Control name={idInput} value={this.state.questions[idInput].text} onChange={this.handleInputChange}></Form.Control>
                )
                ||
                (type === "dropdown" && <Form.Control as="select" onChange={this.handleInputChange}>
                    {
                        choices.map(choice => (
                            <option>{choice.text}</option>
                        ))
                    }
                </Form.Control>)
                ||
                (type === "boolean" && 
                <Fragment>
                    <Form.Check inline label={labelTrue} type="checkbox" onChange={this.handleInputChange}/>
                    <Form.Check inline label={labelFalse} type="checkbox" onChange={this.handleInputChange}/>
                </Fragment>
                )
            }
            </Form.Group>
        )
    }
}

export default OurInput;
