import React, { Component } from 'react';
import './App.css';
import Home from './components/Home';
import NavBar from './components/OurNavBar'
import Advice from './components/Advice';
import Form from './components/Form';
import Graphics from './components/Graphics';
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends Component{
  constructor(){
    super();
    this.state = {
      activeHome: true,
      activeAdvice: false,
      activeForm: false,
      activeGraphics: false
    }
    this.changeActiveComponent = this.changeActiveComponent.bind(this);
  }

  changeActiveComponent(active, noactive){

    this.setState(previousState => {
      return {
        [active]: !previousState.active,
        [noactive]: !previousState.active
      }
    })

  }

  render(){
    return (
      <div className="App">
        <NavBar changeComponent={this.changeActiveComponent}/>
        {
          (this.state.activeHome && <Home/>) ||
          (this.state.activeAdvice && <Advice/>) ||
          (this.state.activeForm && <Form/>) ||
          (this.state.activeGraphics && <Graphics />)
        }
      </div>
    );
  }
}

export default App;
